import React, {useState} from 'react';
import { Header } from './components/layout/Header';
import { Content } from './components/layout/Content';

import { ProjectsProvider, SelectedProjectProvider} from './context';




// export const App = () => {
//   return (
//     <div className="App">
//       <Header />
//       <Content />
//     </div>
//   );
